$(document).ready(function () {
  var swiper2 = new Swiper(".swiper-2", {
    // effect: "fade",
    loop: true,
    pagination: {
      el: ".slide-container-2 .swiper-pagination",
      type: "fraction",
      clickable: true,
    },
    navigation: {
      nextEl: ".slide-container-2 .swiper-button-next",
      prevEl: ".slide-container-2 .swiper-button-prev",
    },
  });

  var swiper1 = new Swiper(".swiper-1", {
    effect: "coverflow",
    grabCursor: true,
    centeredSlides: true,
    slidesPerView: "auto",
    loop: true,
    spaceBetween: 0,
    speed: 500,
    // autoplay: true,

    allowTouchMove: false, // Ngăn chặn người dùng tương tác với swiper
    coverflowEffect: {
      rotate: 30,
      stretch: 0,
      depth: 0,
      modifier: 1,
      slideShadows: true,
    },
    pagination: {
      el: " slide-container-1 .swiper-pagination",
    },
  });

  $(".swiper-slide-next").next().addClass("right-side");
  $(".swiper-slide-prev").prev().addClass("left-side");

  setInterval(function () {
    // currentActive = document.querySelector(".swiper-slide-active");
    // currentActiveIndex = parseInt(currentActive.getAttribute("data-swiper-slide-index"));
    currentRight = document.querySelector(".right-side");
    currentLeft = document.querySelector(".left-side");
    if (currentRight) {
      currentRight.classList.remove("right-side");
    }
    if (currentLeft) {
      currentRight.classList.remove("left-side");
    }
    // /// New
    // querry = `[data-swiper-slide-index="${currentActiveIndex + 1}"]`;
    // nextActive = document.querySelector(querry);
    // newRight = document.querySelector(`[data-swiper-slide-index="${+3}"]`);
    // console.log(currentActiveIndex);
    // console.log(newRight);
    // // newRight.classList.add("right-side");
    swiper1.slideNext();
    $(".swiper-slide-next").next().addClass("right-side");
    $(".swiper-slide-prev").prev().addClass("left-side");
  }, 4000);

  // swiper1.on("slideChange", function () {
  //   // In ra chỉ mục (index) của slide hiện tại sau khi chuyển đổi slide
  //   active = document.querySelector(".swiper-slide-active");
  //   console.log(active);
  // });
});

